\begin{aligned}
    f^*(x) & = \sum_{i=0}^{e} r^i \cdot f_i(x) \\
    & = \sum_{i=0}^{e} r^i \cdot x^i \cdot f(x)\\
    & = f(x) \cdot \sum_{i=0}^{e} (r \cdot x)^i \\
\end{aligned}

f^*(x) = \begin{cases}
    f(x) \cdot \frac{1 - (r \cdot x)^{e+1}}{1 - r \cdot x} &  \text{if} \quad r \cdot x \neq 1 \\
    f(x) \cdot (e + 1) & \text{if} \quad r \cdot x = 1
    \end{cases}
